// Assuming dataGridView1 is your DataGridView, bindingSource1 is your BindingSource, \n// and dataTable1 is the DataTable you initially bound to the DataGridView.\n\nprivate void btnSave_Click(object sender, EventArgs e)\n{\n    // EndEdit on the BindingSource commits changes from the grid to the data source.\n    bindingSource1.EndEdit();\n\n    // Optionally, end edit on the DataTable if necessary (usually not needed unless direct).\n    dataTable1.AcceptChanges();\n    \n    // If you need to update the original DataSet\n    dataSet1.Tables["YourTableName"].AcceptChanges();\n\n    // Now dataTable1 and dataSet1 contain the updated data.\n}