using System.Data;\nusing System.Data.Common;\nusing System.Data.SqlClient;\n\n// Create a SqlConnection and SqlCommand as per your existing setup\nSqlConnection conn = new SqlConnection("your_connection_string");\nSqlCommand comm = new SqlCommand("YourStoredProcedureName", conn);\ncomm.CommandType = CommandType.StoredProcedure;\n\n// Create the parameter and set its type, direction, precision, and scale\nSqlParameter prm = new SqlParameter("@Price", SqlDbType.Decimal) \n{\n    Precision = 18, // Set to the maximum or required precision\n    Scale = 4,      // Set to the required scale\n    Direction = ParameterDirection.Output\n};\ncomm.Parameters.Add(prm);\n\n// Execute your command\nconn.Open();\ncomm.ExecuteNonQuery();\n\n// Retrieve the output value after execution\ndecimal.TryParse(comm.Parameters["@Price"].Value.ToString(), out this.ProdPrice);