using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        using (SqlConnection connection = new SqlConnection("your-connection-string"))\n        {\n            SqlDataAdapter logsAdapter = new SqlDataAdapter(\n                "SELECT * FROM Logs WHERE Timestamp >= DATEADD(DAY, -30, CURRENT_DATE)",\n                connection);\n\n            SqlDataAdapter logDetailsAdapter = new SqlDataAdapter(\n                "SELECT * FROM LogsExtended",\n                connection);\n\n            DataSet dataSet = new DataSet();\n\n            logsAdapter.Fill(dataSet, "Logs");\n            logDetailsAdapter.Fill(dataSet, "LogsExtended");\n\n            DataColumn masterColumn = dataSet.Tables["Logs"].Columns["LogId"];\n            DataColumn detailColumn = dataSet.Tables["LogsExtended"].Columns["LogId"];\n\n            dataSet.Relations.Add("Logs_LogsExtended_Relation", masterColumn, detailColumn);\n\n            // Now you can navigate from Logs to related LogsExtended\n        }\n    }\n}