using System;\nusing System.IO;\n\npublic class FileChecker\n{\n    public static bool IsFileInUse(string filePath)\n    {\n        try\n        {\n            using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.ReadWrite, FileShare.None))\n            {\n                return false; // The file is not in use and we were able to access it.\n            }\n        }\n        catch (IOException e) when ((e.HResult & 0xFFFF) == 32 || (e.HResult & 0xFFFF) == 33)\n        {\n            // File is in use, return true\n            return true;\n        }\n    }\n\n    public static void Main()\n    {\n        string filePath = @"\\network\drive\path\to\file.txt";\n        bool isInUse = IsFileInUse(filePath);\n\n        if (isInUse)\n        {\n            Console.WriteLine("The file is currently in use.");\n        }\n        else\n        {\n            Console.WriteLine("The file is available to use.");\n        }\n    }\n}