private OracleDependency SubscribeToTable(string tableName)\n{\n    string sql = "select * from " + tableName;\n    var cmd = new OracleCommand(sql, this.connection) { AddRowid = true };\n\n    // Specify local IP address for change notifications\n    OracleConfiguration.LocalIPAddress = "YOUR_PHYSICAL_MACHINE_IP_ADDRESS";\n\n    var dep = new OracleDependency(cmd);\n    cmd.Notification.IsNotifiedOnce = false;\n    dep.OnChange += this.dep_OnChange;\n\n    try\n    {\n        cmd.ExecuteNonQuery();\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions here if necessary, e.g., log errors or retries.\n        Console.WriteLine("Error: " + ex.Message);\n    }\n\n    return dep;\n}