// Example within a loop generating Control1s and their embedded Control2s\nfor (int i = 0; i < numberOfControls1Needed; i++)\n{\n    var control1 = LoadControl("~/PathTo/Control1.ascx") as Control1;\n    for (int j = 0; j < numberOfControls2ForThisControl1; j++)\n    {\n        var control2 = LoadControl("~/PathTo/Control2.ascx") as Control2;\n        // Set unique ID\n        control2.ID = $"Control2_{i}_{j}";\n        control1.Controls.Add(control2);\n    }\n    this.myPlaceholder.Controls.Add(control1);  // Assuming myPlaceholder is a PlaceHolder server control in your ASPX page.\n}