protected void Application_PostRequest(object sender, EventArgs e)\n{\n    var path = HttpContext.Current.Request.Path;\n    if (path.EndsWith(".js", StringComparison.OrdinalIgnoreCase) ||\n        path.EndsWith(".css", StringComparison.OrdinalIgnoreCase))\n    {\n        var response = HttpContext.Current.Response;\n        \n        // Cache for 30 days since files change once a month at most\n        response.Cache.SetExpires(DateTime.UtcNow.AddDays(30));\n        response.Cache.SetMaxAge(TimeSpan.FromDays(30)); \n        response.Cache.SetCacheability(HttpCacheability.Public);\n        response.Cache.SetRevalidation(HttpCacheRevalidation.AllCaches);\n        \n        // Add versioning or hash-based query string to CSS and JS links if possible\n        // For example, change <link href="style.css" rel="stylesheet"/> to something like:\n        // <link href="style.css?v=1.0.1" rel="stylesheet"/>\n    }\n}