using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionStringHere";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            string query = @"\n                SELECT \n                    MethodName, \n                    AVG(TimeInMs) AS AverageTimeInMs\n                FROM YourTable\n                WHERE DateTime >= DATEADD(minute, -60, GETDATE())\n                GROUP BY MethodName;";\n\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine($"Method: {reader["MethodName"]}, Average Time: {reader["AverageTimeInMs"]} ms");\n                    }\n                }\n            }\n        }\n    }\n}