using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load your text data source here or use a file path\n        string text = File.ReadAllText("path_to_your_large_text_file.txt");\n\n        // Clean and split the text into words\n        var words = Regex.Split(text.ToLower(), @"\W+")\n                         .Where(s => !string.IsNullOrEmpty(s))\n                         .ToList();\n\n        // Count the frequency of each word\n        var wordCounts = new Dictionary<string, int>();\n        foreach (var word in words)\n        {\n            if (wordCounts.ContainsKey(word))\n                wordCounts[word]++;\n            else\n                wordCounts[word] = 1;\n        }\n\n        // Sort words by their frequency in descending order and take the top 10k\n        var mostCommonWords = wordCounts.ToList()\n                                        .OrderByDescending(pair => pair.Value)\n                                        .ThenBy(pair => pair.Key) // Alphabetical order for ties\n                                        .Take(10000);\n\n        // Output the results\n        foreach (var pair in mostCommonWords)\n        {\n            Console.WriteLine($"{pair.Key}: {pair.Value}");\n        }\n    }\n}