using System;\nusing System.IO;\n\npublic static List<string> GetModifiedFiles(string directoryPath, DateTime sinceDate)\n{\n    var fileInfoList = new List<string>();\n    \n    // Recursively get all files in the specified directory.\n    try\n    {\n        foreach (var filePath in Directory.GetFiles(directoryPath, "*", SearchOption.AllDirectories))\n        {\n            FileInfo fi = new FileInfo(filePath);\n            \n            // Check if the file was modified after the given date.\n            if (fi.LastWriteTimeUtc > sinceDate)\n            {\n                fileInfoList.Add(fi.FullName);\n            }\n        }\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Error accessing files: " + ex.Message);\n    }\n\n    return fileInfoList;\n}