using System;\nusing System.IO;\nusing System.Threading.Tasks;\n\npublic class FileSplitter\n{\n    private async Task CopyAsync(string srcFile, string dstFile, long offset, int length)\n    {\n        using (FileStream source = new FileStream(srcFile, FileMode.Open, FileAccess.Read, FileShare.Read))\n        using (FileStream destination = new FileStream(dstFile, FileMode.Create, FileAccess.Write))\n        {\n            // Seek to the specified offset in the source file\n            await source.SeekAsync(offset, SeekOrigin.Begin);\n\n            // Copy data from source to destination asynchronously\n            await source.CopyToAsync(destination, length);\n        }\n    }\n\n    public async Task SplitFilesAsync(string srcFile, (string DstPath, long Offset, int Length)[] parts)\n    {\n        foreach (var part in parts)\n        {\n            await CopyAsync(srcFile, part.DstPath, part.Offset, part.Length);\n        }\n    }\n}