using System.Diagnostics;\n\npublic void RunRegini(string regFilePath)\n{\n    ProcessStartInfo pi = new ProcessStartInfo();\n    pi.FileName = @"C:\Windows\System32\regini.exe";\n    pi.Arguments = "\"" + regFilePath + "\""; // Ensure the path is enclosed in quotes\n    pi.WorkingDirectory = Environment.SystemDirectory; // Use Environment.SystemDirectory for safety if not using a custom directory\n    pi.WindowStyle = ProcessWindowStyle.Hidden;\n    pi.RedirectStandardError = true;\n    pi.RedirectStandardOutput = true;\n    pi.UseShellExecute = false;\n\n    Process p = new Process();\n    p.StartInfo = pi;\n    p.EnableRaisingEvents = true;\n    try\n    {\n        p.Start();\n        string output = p.StandardOutput.ReadToEnd();\n        string error = p.StandardError.ReadToEnd();\n        p.WaitForExit();\n\n        // Handle output and errors if necessary\n        Console.WriteLine("Output: " + output);\n        Console.WriteLine("Error: " + error);\n    }\n    catch (Exception ex)\n    {\n        // Log or handle exceptions\n        Console.WriteLine("An error occurred: " + ex.Message);\n    }\n}