public class AccountController : Controller\n{\n    [HttpPost]\n    public ActionResult Login(LoginModel model)\n    {\n        if (ModelState.IsValid && IsValidUser(model.Email, model.Password))\n        {\n            // Get user roles based on your logic (database check etc.)\n            string role = "Admin"; // Example role\n\n            Session["UserID"] = model.Email;\n            Session["Role"] = role;\n\n            return RedirectToAction("ManageUsers", "Users");\n        }\n\n        ModelState.AddModelError("", "Failed to log in. Please try again.");\n        return View(model);\n    }\n}