using System.Web.Hosting;\n\npublic class YourClass\n{\n    public string GetAbsolutePath(string virtualPath)\n    {\n        // Ensure the virtual path starts with a tilde (~) to represent the root of the application.\n        if (!virtualPath.StartsWith("~"))\n        {\n            throw new ArgumentException("The virtual path must start with a tilde (~).", nameof(virtualPath));\n        }\n\n        string absolutePath = HostingEnvironment.MapPath(virtualPath);\n        return absolutePath;\n    }\n}