using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Constants for SetWindowLong function\n    private const int GWL_STYLE = -16;\n    private const uint WS_POPUP = 0x80000000;\n    private const uint WS_CAPTION = 0xC00000;          // WS_BORDER | WS_DLGFRAME\n    private const uint WS_SYSMENU = 0x80000;\n    private const uint WS_THICKFRAME = 0x40000;\n\n    [DllImport("user32.dll")]\n    static extern IntPtr GetWindowLong(IntPtr hWnd, int nIndex);\n\n    [DllImport("user32.dll")]\n    static extern int SetWindowLong(IntPtr hWnd, int nIndex, int dwNewLong);\n\n    public static void MakeBorderless(IntPtr hwnd)\n    {\n        // Retrieve the current window style\n        long currentStyle = (long)GetWindowLong(hwnd, GWL_STYLE);\n        \n        // Modify the window style to remove border and title bar\n        SetWindowLong(hwnd, GWL_STYLE, (int)(currentStyle & ~(WS_CAPTION | WS_SYSMENU | WS_THICKFRAME)));\n    }\n\n    static void Main()\n    {\n        IntPtr hwnd = new IntPtr(/* Your window handle here */);\n        MakeBorderless(hwnd);\n    }\n}