// Master Computer\npublic void StartSynchronization()\n{\n    // Send start command to slaves\n    foreach (var client in connectedClients)\n    {\n        client.Send("START");\n    }\n}\n\n// Slave Computer\nprivate void OnNetworkMessageReceived(string message)\n{\n    if (message == "START")\n    {\n        // Ensure all clients have accurate time synchronization\n        var currentTime = GetNTPTime();\n        \n        // Calculate offset based on network latency etc.\n        var playOffset = CalculatePlayOffset(currentTime);\n        \n        // Start playing video with calculated offset \n        mediaElement.Position = TimeSpan.FromMilliseconds(playOffset);\n        mediaElement.Play();   \n    }\n}\n\n// Utility function to get NTP time\nprivate DateTime GetNTPTime()\n{\n    // Logic to fetch network time\n}