using System;\nusing HtmlAgilityPack;\n\npublic class HtmlValidator\n{\n    public static bool AreTagsProperlyClosed(string htmlContent)\n    {\n        var doc = new HtmlDocument();\n        doc.LoadHtml(htmlContent);\n\n        // Check for any unclosed tags by verifying the parsed document structure.\n        // If the parsing produces warnings or errors, it might indicate bad HTML formatting.\n        bool hasErrors = false;\n        foreach (var error in doc.ParseErrors)\n        {\n            if (error.Code != HtmlParseErrorCode.MisplacedAttribute &&\n                error.Code != HtmlParseErrorCode.UnclosedServerSideTag)\n            {\n                hasErrors = true;\n                Console.WriteLine($"Error: {error.Reason}");\n            }\n        }\n\n        return !hasErrors;\n    }\n\n    public static void Main(string[] args)\n    {\n        string userHtmlContent = "<div><p>Test content</p>";\n        bool isValid = AreTagsProperlyClosed(userHtmlContent);\n        if (isValid)\n        {\n            Console.WriteLine("All tags are properly closed.");\n        }\n        else\n        {\n            Console.WriteLine("Some tags may be unclosed or incorrectly formatted.");\n        }\n    }\n}