using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    private static void Main()\n    {\n        List<string> list = new List<string> { "Box", "Gate", "Car" };\n        string searchString = "box"; // This could be user input\n\n        int index = FindIndex(list, searchString);\n        Console.WriteLine($"Index of '{searchString}': {index}");\n    }\n\n    private static int FindIndex(List<string> list, string searchString)\n    {\n        Predicate<string> predicate = new Predicate<string>(item => item.Equals(searchString, StringComparison.OrdinalIgnoreCase));\n        return list.FindIndex(predicate);\n    }\n}