using System;\nusing System.Xml;\n\nnamespace TempConsoleApp\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string text = "<?xml version=\"1.0\"?><tag1><tag2>Some text.</taagg2></tag1>";\n            int index = FindIndexOfInvalidXml(text);\n            Console.WriteLine(index);\n        }\n\n        private static int FindIndexOfInvalidXml(string theString)\n        {\n            try\n            {\n                XmlDocument xmlDoc = new XmlDocument();\n                xmlDoc.LoadXml(theString);\n            }\n            catch (XmlException ex)\n            {\n                // Calculate the approximate character index from line and column number\n                return GetCharIndexFromLineColumn(theString, ex.LineNumber, ex.LinePosition);\n            }\n\n            // If no exception, the XML is well-formed.\n            return -1;\n        }\n\n        private static int GetCharIndexFromLineColumn(string text, int lineNumber, int linePosition)\n        {\n            int currentLine = 1;\n            int currentIndex = 0;\n\n            while (currentLine < lineNumber && currentIndex < text.Length)\n            {\n                if (text[currentIndex] == '\n')\n                {\n                    currentLine++;\n                }\n                currentIndex++;\n            }\n\n            return currentIndex + linePosition - 1; // Adjusting for 1-based column number\n        }\n    }\n}