using Aspose.Pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load an existing PDF document or create a new one\n        Document pdfDocument = new Document("sample.pdf");\n\n        // Encrypt the document with user and owner passwords\n        string userPassword = "user_password";\n        string ownerPassword = "owner_password";\n        EncryptionOptions encryptionOptions = pdfDocument.Encrypt(userPassword, ownerPassword);\n\n        // Restrict permissions: allow only printing or modifying content\n        encryptionOptions.Permissions = PermissionFlags.PrintDocument | PermissionFlags.ModifyContent;\n\n        // Save the encrypted PDF document\n        pdfDocument.Save("Encrypted_sample.pdf");\n    }\n}