using System;\nusing System.IO;\n\npublic class MyService : ServiceBase\n{\n    protected override void OnStart(string[] args)\n    {\n        // Redirect console output to a file\n        var logFile = new FileStream("C:\\MyServiceLogs\\log.txt", FileMode.Append, FileAccess.Write);\n        var writer = new StreamWriter(logFile) { AutoFlush = true };\n        Console.SetOut(writer);\n\n        // Start your service logic here...\n    }\n\n    protected override void OnStop()\n    {\n        // Ensure the file is closed properly when the service stops\n        ((IDisposable)Console.Out).Dispose();\n    }\n}