using System.Xml;\nusing System.Xml.Xsl;\n\nclass Program\n{\n    static void Main()\n    {\n        XslCompiledTransform xslt = new XslCompiledTransform();\n        xslt.Load("YourXsltFile.xsl"); // Load your XSLT file\n\n        XPathDocument xpathDoc = new XPathDocument("YourXmlFile.xml");\n        XmlTextWriter writer = new XmlTextWriter("Output.xml", null);\n        xslt.Transform(xpathDoc, null, writer); // Perform the transform\n    }\n}