using System;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class SmsClientApp\n{\n    public void ConnectToSmsServer()\n    {\n        TcpClient client = new TcpClient("WindowsMobileIP", 5000);\n        NetworkStream stream = client.GetStream();\n\n        byte[] bytes = new byte[1024];\n        int bytesRead = stream.Read(bytes, 0, bytes.Length);\n\n        string dataReceived = Encoding.UTF8.GetString(bytes, 0, bytesRead);\n        Console.WriteLine("Data from SMS Server: " + dataReceived);\n\n        stream.Close();\n        client.Close();\n    }\n}