using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your connection strings\n        string connectionString1 = "User Id=id1;Password=pwd1;Data Source=source1;";\n        string connectionString2 = "User Id=id2;Password=pwd2;Data Source=source2;";\n\n        // Select the connection string at runtime, for example based on an input or configuration\n        bool useFirstSchema = true; // This could be set dynamically in a real application\n        string selectedConnectionString = useFirstSchema ? connectionString1 : connectionString2;\n\n        // Create and configure the Oracle data adapter with the selected connection string\n        using (OracleConnection conn = new OracleConnection(selectedConnectionString))\n        {\n            string query = "SELECT * FROM your_table";\n            OracleDataAdapter da = new OracleDataAdapter(query, conn);\n\n            // Fill the dataset with data from the selected database\n            DataSet ds = new DataSet();\n            da.Fill(ds);\n\n            // Now ds contains data from either schema depending on the connection string used\n        }\n    }\n}