using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load your XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load("path_to_your_file.xml");\n\n        // Select the nodes using XPath\n        XmlNodeList nodesToModify = xmlDoc.SelectNodes("//yourXPathExpression");\n\n        if (nodesToModify != null)\n        {\n            foreach (XmlNode node in nodesToModify)\n            {\n                // Modify the InnerText or other properties\n                node.InnerText = "New Value";\n            }\n        }\n\n        // Save the modified XML back to a file\n        xmlDoc.Save("path_to_your_modified_file.xml");\n    }\n}