public string Post(string url, string data)\n{\n    string result = "";\n    try\n    {\n        byte[] buffer = Encoding.GetEncoding(1252).GetBytes(data);\n        HttpWebRequest WebReq = (HttpWebRequest)WebRequest.Create(url); // No need for @ before the URL\n\n        WebReq.Method = "POST";\n        WebReq.ContentType = "application/x-www-form-urlencoded";              \n        WebReq.ContentLength = buffer.Length;\n\n        using (Stream PostData = WebReq.GetRequestStream())\n        {\n            PostData.Write(buffer, 0, buffer.Length);\n        }\n\n        using (HttpWebResponse WebResp = (HttpWebResponse)WebReq.GetResponse())\n        using (Stream Answer = WebResp.GetResponseStream())\n        using (StreamReader _Answer = new StreamReader(Answer))\n        {\n            result = _Answer.ReadToEnd();\n        }\n    }\n    catch (Exception ex)\n    {\n        MessageBox.Show(ex.Message);\n    }\n\n    return result;\n}