using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private WebBrowser webBrowser;\n\n    public MyForm()\n    {\n        webBrowser = new WebBrowser();\n        webBrowser.Dock = DockStyle.Fill;\n        Controls.Add(webBrowser);\n\n        // Subscribe to the NewWindow event\n        webBrowser.NewWindow += WebBrowser_NewWindow;\n        \n        // Navigate to an initial URL\n        webBrowser.Navigate("http://example.com");\n    }\n\n    private void WebBrowser_NewWindow(object sender, CancelEventArgs e)\n    {\n        // Cancel the new window creation\n        e.Cancel = true;\n\n        // Open the URL in the same WebBrowser control\n        if (webBrowser.StatusText.Length > 0 && Uri.IsWellFormedUriString(webBrowser.StatusText, UriKind.Absolute))\n        {\n            webBrowser.Navigate(new Uri(webBrowser.StatusText));\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MyForm());\n    }\n}