using System;\nusing System.Net;\n\npublic class WebServiceClient\n{\n    private HttpWebRequest request;\n\n    public void ConfigureRequest(string url)\n    {\n        request = (HttpWebRequest)WebRequest.Create(url);\n        \n        // Use the system's default proxy settings\n        request.Proxy = WebRequest.DefaultWebProxy;\n        \n        // Use cached credentials for authentication if required\n        request.Credentials = CredentialCache.DefaultCredentials;\n        request.PreAuthenticate = true;\n\n        try\n        {\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                Console.WriteLine("Response status: " + response.StatusDescription);\n            }\n        }\n        catch (WebException we)\n        {\n            Console.Error.WriteLine("Error: " + we.Message);\n            if (we.Response is HttpWebResponse)\n            {\n                HttpWebResponse errorResponse = we.Response as HttpWebResponse;\n                Console.Error.WriteLine("HTTP error code: " + errorResponse.StatusCode);\n            }\n        }\n    }\n}