using System.ComponentModel.Composition;\n\n// Define an interface for extension points\npublic interface IExtensionFunctionality\n{\n    void DoSomething();\n}\n\n// Core component that consumes extensions\n[Export]\npublic class CoreComponent\n{\n    [ImportMany]\n    public IEnumerable<IExtensionFunctionality> Extensions { get; set; }\n\n    public void ExecuteExtensions()\n    {\n        foreach (var extension in Extensions)\n            extension.DoSomething();\n    }\n}\n\n// An example of an extension\n[Export(typeof(IExtensionFunctionality))]\npublic class MyCustomFunctionality : IExtensionFunctionality\n{\n    public void DoSomething()\n    {\n        // Custom behavior\n    }\n}