[Test]\npublic void TestIndexRoute()\n{\n    // Create route collection\n    RouteCollection routes = new RouteCollection();\n    \n    // Register routes (assuming your RegisterRoutes method is in MvcApplication class)\n    YourNamespace.MvcApplication.RegisterRoutes(routes);\n    \n    // Confirm that routes are registered and default route exists\n    Assert.IsTrue(routes.Count > 0, "No routes were registered.");\n    Assert.IsNotNull(routes["Default"], "Default route not found.");\n\n    // Test the route matching for "/Author"\n    var result = RouteTest.New("/Author")\n        .WithDefaultRoute()\n        .ShouldMapTo<AuthorController>(x => x.Index());\n\n    // Alternatively, if you are sticking with RouteTestingExtensions:\n    var routeData = routes.GetRouteData(new HttpContextWrapper(new MockHttpContext("~/Author", "")));\n    \n    Assert.IsNotNull(routeData);\n    Assert.AreEqual("Author", (string)routeData.Values["controller"]);\n    Assert.AreEqual("Index", (string)routeData.Values["action"]);\n}