using System;\n     using System.CommandLine;\n\n     class Program\n     {\n         static int Main(string[] args)\n         {\n             var rootCommand = new RootCommand("My Command Line Application")\n             {\n                 new Option<string>("--input", description: "Input file path") { IsRequired = true },\n                 new Option<string>("--output", getDefaultValue: () => "default.txt", description: "Output file path"),\n             };\n\n             rootCommand.SetHandler((string input, string output) =>\n             {\n                 Console.WriteLine($"Processing {input}, writing to {output}...");\n             }, rootCommand.Options[0], rootCommand.Options[1]);\n\n             return rootCommand.InvokeAsync(args).Result;\n         }\n     }