private void Explorer_MouseOverEvent(mshtml.IHTMLEventObj e)\n{\n    if (e == null) return;\n\n    mshtml.IHTMLDocument2 doc = this.Explorer.Document as IHTMLDocument2;\n    if (doc == null) return;\n\n    mshtml.IHTMLElement element = doc.elementFromPoint(e.clientX, e.clientY);\n    if (element != null && element.tagName.Equals("DIV", StringComparison.InvariantCultureIgnoreCase))\n    {\n        element.style.border = "thin solid blue";\n        string className = element.className;\n        if (!string.IsNullOrEmpty(className))\n        {\n            UpdateToolstrip(className);\n        }\n    }\n}\n\ndelegate void UpdateToolstripDelegate(string text);\n\npublic void UpdateToolstrip(string text)\n{\n    try\n    {\n        if (this.toolStripLabel1.InvokeRequired)\n        {\n            this.toolStripLabel1.Invoke(new UpdateToolstripDelegate(UpdateToolstrip), new object[] { text });\n        }\n        else\n        {\n            this.toolStripLabel1.Text = text;\n        }\n    }\n    catch (Exception ex)\n    {\n        // Log the exception or handle it appropriately\n        Debug.WriteLine($"Error updating toolbar: {ex.Message}");\n    }\n}