using NUnit.Framework;\nusing System.Data.SQLite;\n\n[TestFixture]\npublic class IntegrationTests\n{\n    private SQLiteConnection _dbConnection;\n\n    [SetUp]\n    public void Setup()\n    {\n        // Initialize the in-memory SQLite DB connection once per test fixture\n        _dbConnection = new SQLiteConnection("Data Source=:memory:");\n        _dbConnection.Open();\n        \n        // Setup schema...\n        using (var command = new SQLiteCommand(_dbConnection))\n        {\n            command.CommandText = "CREATE TABLE TestTable (Id INT PRIMARY KEY, Name TEXT);";\n            command.ExecuteNonQuery();\n        }\n    }\n\n    [Test]\n    public void IntegrationTestCase()\n    {\n        // Write your test case here which uses _dbConnection\n        using (var command = new SQLiteCommand(_dbConnection))\n        {\n            command.CommandText = "INSERT INTO TestTable(Id, Name) VALUES (1, 'Test');";\n            command.ExecuteNonQuery();\n\n            command.CommandText = "SELECT COUNT(*) FROM TestTable;";\n            int count = (int)command.ExecuteScalar();\n            \n            Assert.AreEqual(1, count);\n        }\n    }\n\n    [TearDown]\n    public void TearDown()\n    {\n        _dbConnection.Close();\n    }\n}