using System.Data;\nusing FarPoint.Win.Spread;\n\n// Assuming 'dataReader' is your SqlDataReader and 'fpSpread' is your Farpoint Spread control\nDataTable dataTable = new DataTable();\n\ntry {\n    if (dataReader != null && dataReader.HasRows) {\n        // Load the DataReader into a DataTable\n        dataTable.Load(dataReader);\n        \n        // Set the DataSource of the spread sheet to the DataTable\n        fpSpread.DataSource = dataTable;\n    }\n} finally {\n    if (dataReader != null) {\n        dataReader.Close();\n    }\n}