using System.Globalization;\nusing YourResourceNamespace;\n\nnamespace YourApp.Utilities\n{\n    public static class LocalizationUtility\n    {\n        // Method to get a localized string from resource files\n        public static string GetLocalizedString(string key, CultureInfo cultureInfo = null)\n        {\n            try\n            {\n                if (cultureInfo == null)\n                    cultureInfo = CultureInfo.CurrentUICulture;\n\n                return GlobalResource.ResourceManager.GetString(key, cultureInfo);\n            }\n            catch\n            {\n                // Handle missing keys or other exceptions as needed\n                return $"Localization key '{key}' not found.";\n            }\n        }\n\n        // Overload for default culture\n        public static string GetLocalizedString(string key)\n        {\n            return GetLocalizedString(key, CultureInfo.CurrentUICulture);\n        }\n    }\n}