using (var session = sessionFactory.OpenSession())\n    {\n        using (var transaction = session.BeginTransaction())\n        {\n            foreach (var permission in Enum.GetValues(typeof(Permission)).Cast<Permission>())\n            {\n                var permissionDescription = GetEnumDescription(permission);\n                var existingPermission = session.Query<Permissions>().FirstOrDefault(p => p.PermissionId == (int)permission);\n\n                if (existingPermission == null)\n                {\n                    session.Save(new Permissions { PermissionId = (int)permission, PermissionName = permissionDescription });\n                }\n            }\n\n            transaction.Commit();\n        }\n    }\n\n    private static string GetEnumDescription(Enum value)\n    {\n        var fieldInfo = value.GetType().GetField(value.ToString());\n        var attributes = (StringValue[])fieldInfo.GetCustomAttributes(typeof(StringValue), false);\n        return attributes.Length > 0 ? attributes[0].Value : value.ToString();\n    }