using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        using (var client = new HttpClient())\n        {\n            // URL to which you need to send the request.\n            string url = "http://example.com/form-submit-endpoint";\n\n            // Form content including the dropdown option.\n            var formData = new FormUrlEncodedContent(new[]\n            {\n                new KeyValuePair<string, string>("dropdownName", "secondOptionValue")\n            });\n\n            // Sending POST request with form data\n            HttpResponseMessage response = await client.PostAsync(url, formData);\n\n            if (response.IsSuccessStatusCode)\n            {\n                string result = await response.Content.ReadAsStringAsync();\n                Console.WriteLine(result);\n            }\n            else\n            {\n                Console.WriteLine($"Request failed with status code: {response.StatusCode}");\n            }\n        }\n    }\n}