[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult SaveImage(string file, string fileName)\n{\n    if (string.IsNullOrEmpty(file) || string.IsNullOrEmpty(fileName))\n    {\n        return new HttpStatusCodeResult(HttpStatusCode.BadRequest, "File and FileName cannot be null or empty");\n    }\n\n    try\n    {\n        // Decode the Base64 encoded string into a byte array\n        byte[] imageBytes = Convert.FromBase64String(file);\n\n        // Define the path where to save the file\n        string pathToSave = Server.MapPath("~/Images/" + fileName); // Adjust the path as needed\n\n        // Ensure the directory exists\n        Directory.CreateDirectory(Path.GetDirectoryName(pathToSave));\n\n        // Write the bytes to a file\n        System.IO.File.WriteAllBytes(pathToSave, imageBytes);\n\n        return new HttpStatusCodeResult(HttpStatusCode.OK, "Image saved successfully");\n    }\n    catch (Exception ex)\n    {\n        // Log the exception details\n        Console.WriteLine("Error saving image: " + ex.Message);\n        \n        return new HttpStatusCodeResult(HttpStatusCode.InternalServerError, "Failed to save image");\n    }\n}