public class PopulateModelBaseDataActionFilter : ActionFilterAttribute\n{\n    public override void OnActionExecuted(ActionExecutedContext filterContext)\n    {\n        var model = filterContext.Controller.ViewData.Model as ModelBase;\n        if (model != null)\n        {\n            // Access session here, typically via a service or directly\n            model.PartnerId = filterContext.HttpContext.Session["partnerId"].ToString();\n            model.CurrentLoggedInUserEmail = filterContext.HttpContext.Session["currentLoggedInUserEmail"].ToString();\n        }\n    }\n}