protected void TimeInButton_Click(object sender, EventArgs e)\n{\n    Button timeInButton = (Button)sender;\n    GridViewRow row = (GridViewRow)timeInButton.NamingContainer;\n\n    // Find the Time Out button in the same row and make it visible\n    Button timeOutButton = (Button)row.FindControl("TimeOutButton");\n    if (timeOutButton != null)\n    {\n        timeOutButton.Visible = true;\n        // Hide the Time In button\n        timeInButton.Visible = false;\n    }\n}\n\nprotected void TimeOutButton_Click(object sender, EventArgs e)\n{\n    Button timeOutButton = (Button)sender;\n    GridViewRow row = (GridViewRow)timeOutButton.NamingContainer;\n\n    // Find the Time In button in the same row and make it visible\n    Button timeInButton = (Button)row.FindControl("TimeInButton");\n    if (timeInButton != null)\n    {\n        timeInButton.Visible = true;\n        // Hide the Time Out button\n        timeOutButton.Visible = false;\n    }\n}