using System;\nusing SHDocVw;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new instance of Internet Explorer\n        InternetExplorer ie = new InternetExplorer();\n        ie.Navigate("http://example.com");\n\n        // Wait for the page to finish loading\n        while (ie.Busy || ie.ReadyState != tagREADYSTATE.READYSTATE_COMPLETE)\n        {\n            System.Threading.Thread.Sleep(100);\n        }\n\n        // Execute a "Save As" command to save it as MHT\n        object empty = Type.Missing;\n        InternetExplorerMedium iem = (InternetExplorerMedium)ie;\n\n        // Command constants can be found in SHDocVw.dll\n        iem.ExecWB(OLECMDEXECOPT.OLECMDEXECOPT_DODEFAULT, 17); // 17 corresponds to IDM_FILE_SAVEAS\n\n        // Cleanup\n        ie.Quit();\n    }\n}