using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "The quick brown fox jumps over the lazy dog.";\n        string pattern = @"\b\w{4}\b"; // Matches words with exactly four letters\n\n        string result = Regex.Replace(input, pattern, ReplaceCallback);\n\n        Console.WriteLine(result);\n    }\n\n    static string ReplaceCallback(Match match)\n    {\n        // Access the original string\n        String originalString = match.Context.Input;\n\n        // Perform any logic you need here based on the match\n        return $"({match.Value})";\n    }\n}