using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Declare the P/Invoke signatures for the UPS control functions.\n    [DllImport("upssdk.dll", CallingConvention = CallingConvention.Cdecl)]\n    static extern int UPSSDK_Init();\n\n    [DllImport("upssdk.dll", CallingConvention = CallingConvention.Cdecl)]\n    static extern int UPSSDK_TurnOffUPS();\n\n    static void Main()\n    {\n        int result;\n\n        // Initialize the UPS SDK.\n        result = UPSSDK_Init();\n        if (result != 0)\n        {\n            Console.WriteLine("Failed to initialize UPS SDK.");\n            return;\n        }\n\n        // Attempt to turn off the UPS.\n        result = UPSSDK_TurnOffUPS();\n        if (result != 0)\n        {\n            Console.WriteLine("Failed to shut down UPS.");\n        }\n        else\n        {\n            Console.WriteLine("UPS shutdown initiated successfully.");\n        }\n    }\n}