using System;\nusing System.IO;\n\nnamespace YourNamespace\n{\n    public partial class YourPage : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                // Specify the path to your file\n                string filePath = Server.MapPath("~/path/to/yourfile.html");\n\n                try\n                {\n                    string fileContents = File.ReadAllText(filePath);\n                    \n                    // Set the contents to the div\n                    contentDiv.InnerHtml = Server.HtmlEncode(fileContents); // Use HtmlEncode for security, especially if the file is untrusted\n                }\n                catch (Exception ex)\n                {\n                    contentDiv.InnerText = "Error reading the file: " + ex.Message;\n                }\n            }\n        }\n    }\n}