using System;\nusing System.Net;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Setup the callback to trust specific certificates\n        ServicePointManager.ServerCertificateValidationCallback = TrustSpecificCertificate;\n\n        try\n        {\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://your-api-url.com");\n            using (var response = (HttpWebResponse)request.GetResponse())\n            {\n                Console.WriteLine(response.StatusCode);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine(ex.ToString());\n        }\n    }\n\n    private static bool TrustSpecificCertificate(object sender, X509Certificate certificate, \n                                                  X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        if (sslPolicyErrors == SslPolicyErrors.None)\n            return true;\n\n        // Check for specific certificate thumbprint\n        var certThumbprint = "YOUR-TUMBNAIL-HERE";\n        return certificate.GetCertHashString().Equals(certThumbprint, StringComparison.OrdinalIgnoreCase);\n    }\n}