using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class WirelessNetworkListPanel : FlowLayoutPanel {\n    public WirelessNetworkListPanel() {\n        Dock = DockStyle.Fill;\n        AutoScroll = true;\n        Padding = new Padding(10);\n    }\n\n    // Method to add network items\n    public void AddWirelessNetwork(string ssid, string signalStrength, bool isConnected) {\n        var panel = new Panel {\n            BorderStyle = BorderStyle.FixedSingle,\n            Size = new Size(this.ClientSize.Width - 22, 70),\n            Margin = new Padding(5)\n        };\n\n        var nameLabel = new Label {\n            Text = $"Network: {ssid}",\n            AutoSize = true,\n            Location = new Point(10, 10)\n        };\n        panel.Controls.Add(nameLabel);\n\n        var signalLabel = new Label {\n            Text = $"Signal Strength: {signalStrength}%",\n            AutoSize = true,\n            Location = new Point(10, 35)\n        };\n        panel.Controls.Add(signalLabel);\n\n        if (isConnected) {\n            var connectedLabel = new Label {\n                Text = "Connected",\n                AutoSize = true,\n                ForeColor = Color.Green,\n                Location = new Point(panel.ClientSize.Width - 200, 10)\n            };\n            panel.Controls.Add(connectedLabel);\n        }\n\n        this.Controls.Add(panel);\n    }\n}\n\nclass MainForm : Form {\n    public MainForm() {\n        var wifiList = new WirelessNetworkListPanel();\n        wifiList.AddWirelessNetwork("MyWiFiNetwork", "85", true);\n        wifiList.AddWirelessNetwork("GuestNetwork", "60", false);\n        this.Controls.Add(wifiList);\n    }\n}\n\nstatic class Program {\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MainForm());\n    }\n}