using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TripleDESEncryption\n{\n    public static string EncryptTripleDES(string inputText)\n    {\n        // Ensure the key is 24 bytes (192 bits) long for TripleDES\n        byte[] key = new byte[] { 0x41, 0x42, 0x43, 0x44, 0x45, 0x46, 0x47, 0x48, \n                                  0x31, 0x32, 0x33, 0x34, 0x35, 0x36, 0x37, 0x38, \n                                  0x41, 0x42, 0x43, 0x44, 0x45, 0x46, 0x47, 0x48 };\n        byte[] clearData = Encoding.UTF8.GetBytes(inputText);\n\n        using (MemoryStream ms = new MemoryStream())\n        {\n            using (TripleDES alg = TripleDES.Create())\n            {\n                alg.Key = key;\n                // It's generally not recommended to use ECB mode due to security risks\n                alg.Mode = CipherMode.ECB;\n\n                ICryptoTransform encryptor = alg.CreateEncryptor();\n\n                using (CryptoStream cs = new CryptoStream(ms, encryptor, CryptoStreamMode.Write))\n                {\n                    cs.Write(clearData, 0, clearData.Length);\n                    cs.FlushFinalBlock();\n                }\n\n                byte[] cipherBytes = ms.ToArray();\n                return Convert.ToBase64String(cipherBytes);\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string textToEncrypt = "Hello, World!";\n        string encryptedText = EncryptTripleDES(textToEncrypt);\n        Console.WriteLine("Encrypted Text: " + encryptedText);\n    }\n}