using System.Diagnostics;\n\nprotected void RestartIISButton_Click(object sender, EventArgs e)\n{\n    ProcessStartInfo startInfo = new ProcessStartInfo("cmd.exe", "/C iisreset")\n    {\n        Verb = "runas", // As administrator\n        RedirectStandardOutput = false,\n        UseShellExecute = true,\n        CreateNoWindow = false\n    };\n    \n    try\n    {\n        Process.Start(startInfo);\n        Response.Write("IIS resetting...");\n    }\n    catch (Exception ex)\n    {\n        Response.Write($"Error: {ex.Message}");\n    }\n}