using System;\nusing System.Web.UI;\n\nnamespace MyCustomControls\n{\n    [ToolboxData("<{0}:MyButton runat='server'></{0}:MyButton>")]\n    public class MyButton : WebControl, IPostBackEventHandler\n    {\n        public string ButtonText\n        {\n            get { return ViewState["ButtonText"] as string ?? "Click Me"; }\n            set { ViewState["ButtonText"] = value; }\n        }\n\n        protected override void CreateChildControls()\n        {\n            Controls.Clear();\n            var button = new System.Web.UI.WebControls.Button();\n            button.Text = ButtonText;\n            button.Click += Button_Click;\n            Controls.Add(button);\n        }\n\n        private void Button_Click(object sender, EventArgs e)\n        {\n            // Handle click event\n            RaisePostBackEvent(string.Empty);\n        }\n\n        public void RaisePostBackEvent(string eventArgument)\n        {\n            OnClick(EventArgs.Empty);\n        }\n    }\n}