using System;\nusing System.Data;\n\npublic class XmlToDataSetConverter\n{\n    public static DataSet ConvertXmlToDataSet(string xmlFilePath)\n    {\n        // Create a new DataSet.\n        DataSet dataSet = new DataSet();\n\n        // Read XML schema file if you have one. Otherwise, rely on XML inferencing.\n        // dataSet.ReadXmlSchema("schema.xsd");\n\n        // Read the data from the XML file into the DataSet.\n        dataSet.ReadXml(xmlFilePath);\n\n        // Optionally, print out table names and relationships for verification\n        Console.WriteLine("DataSet Tables:");\n        foreach (DataTable table in dataSet.Tables)\n        {\n            Console.WriteLine(" - " + table.TableName);\n        }\n\n        Console.WriteLine("\nDataSet Relationships:");\n        foreach (DataRelation relation in dataSet.Relations)\n        {\n            Console.WriteLine($" - {relation.ParentTable.TableName} -> {relation.ChildTable.TableName}");\n        }\n\n        return dataSet;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlFilePath = "path_to_your_complex_structure.xml";\n        DataSet dataSet = XmlToDataSetConverter.ConvertXmlToDataSet(xmlFilePath);\n        // Now you can work with the dataSet as needed.\n    }\n}